Script.Body:
void ToggleTextFrame()
{
	SceneLayer@ UILayer = Scene.GetLayer("Novel interface");
	if (UILayer !is null)
	{
		bool bVisible = UILayer.GetRootObject().visible;
		UILayer.GetRootObject().visible = !bVisible;

		Button@ BtnAll = GetObject("Button:All") as Button;
		if (BtnAll !is null)
			BtnAll.enabled = bVisible;
	}
}
